package br.edu.univas.lab6.smartpoll.entity;

import java.util.Date;

import javax.persistence.Column;
import javax.persistence.Entity;
import javax.persistence.FetchType;
import javax.persistence.GeneratedValue;
import javax.persistence.GenerationType;
import javax.persistence.Id;
import javax.persistence.JoinColumn;
import javax.persistence.ManyToOne;
import javax.persistence.SequenceGenerator;
import javax.persistence.Table;
import javax.persistence.Temporal;
import javax.persistence.TemporalType;

/**
 * Result generated by hbm2java
 */
@Entity
@Table(name = "result", schema = "public")
public class Result implements java.io.Serializable {

	/**
	 * 
	 */
	private static final long serialVersionUID = 1L;

	private int id;
	private Question question;
	private Answer answer;
	private Date dateVote;

	public Result() {
	}

	public Result(int id, Question question, Answer answer, Date dateVote) {
		this.id = id;
		this.question = question;
		this.answer = answer;
		this.dateVote = dateVote;
	}

	@Id
	@Column(name = "id", unique = true, nullable = false)
	@GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "result_gen")
	@SequenceGenerator(name = "result_gen", sequenceName = "result_id_seq", allocationSize = 1)
	public int getId() {
		return this.id;
	}

	public void setId(int id) {
		this.id = id;
	}

	@ManyToOne(fetch = FetchType.LAZY)
	@JoinColumn(name = "question_id", nullable = false)
	public Question getQuestion() {
		return this.question;
	}

	public void setQuestion(Question question) {
		this.question = question;
	}

	@ManyToOne(fetch = FetchType.LAZY)
	@JoinColumn(name = "answer_id", nullable = false)
	public Answer getAnswer() {
		return this.answer;
	}

	public void setAnswer(Answer answer) {
		this.answer = answer;
	}

	@Temporal(TemporalType.TIMESTAMP)
	@Column(name = "date_vote", nullable = false, length = 29)
	public Date getDateVote() {
		return this.dateVote;
	}

	public void setDateVote(Date dateVote) {
		this.dateVote = dateVote;
	}

}
